import React, { useState } from 'react';
import "../../cssFiles/cm.css";
import { setData } from '../../database/database';
import searchIcon from '../../images/search.png';

export function ClientManager(props) {
  const clientData = JSON.parse(props.userData.clientData);
  const groupData = props.userData.groupData;

  const [showAddClientForm, setShowAddClientForm] = useState(false);
  // Initializing with current time
  const now = new Date();
  const [year, setYear] = useState(now.getFullYear());
  const [month, setMonth] = useState(now.getMonth() + 1);
  const [day, setDay] = useState(now.getDate());
  const [hour, setHour] = useState(now.getHours() % 12 || 12);
  const [minute, setMinute] = useState(now.getMinutes());
  const [amPm, setAmPm] = useState(now.getHours() >= 12 ? 'PM' : 'AM');

  const toggleAddClientForm = () => setShowAddClientForm(!showAddClientForm);

  const generateOptions = (start, end) => {
    let options = [];
    for (let i = start; i <= end; i++) {
      options.push(<option key={i} value={i}>{i}</option>);
    }
    return options;
  };

  return (
    <>
      <div className="cm-top-headers">
        <div id="cm-toggle-add-client" onClick={toggleAddClientForm}>Add Client</div>
        <div id="cm-search"><h1>Search</h1><img id='cm-search-icon' src={searchIcon} /></div>
      </div>

      {showAddClientForm && (
        <div className="userblock" id="addClientBlock">
          <input type="text" className="input-field" placeholder="First Name"/>
          <input type="text" className="input-field" placeholder="Last Name"/>
          <input type="text" className="input-field" placeholder="Phone Number"/>

          <select className="date-time-input" value={month} onChange={(e) => setMonth(e.target.value)}>
            {generateOptions(1, 12)}
          </select>
          <select className="date-time-input" value={day} onChange={(e) => setDay(e.target.value)}>
            {generateOptions(1, 31)}
          </select>
          <select className="date-time-input" value={year} onChange={(e) => setYear(e.target.value)}>
            {generateOptions(now.getFullYear() - 5, now.getFullYear() + 5)}
          </select>
          <select className="date-time-input" value={hour} onChange={(e) => setHour(e.target.value)}>
            {generateOptions(1, 12)}
          </select>
          <select className="date-time-input" value={minute} onChange={(e) => setMinute(e.target.value)}>
            {generateOptions(0, 59)}
          </select>
          <select className="date-time-input" value={amPm} onChange={(e) => setAmPm(e.target.value)}>
            <option value="AM">AM</option>
            <option value="PM">PM</option>
          </select>

          <div className="editstack">
            <button className="addClient">Confirm Client</button>
          </div>
        </div>
      )}

      {/* Client blocks rendering logic remains unchanged */}
    </>
  );
}
