import { useState } from 'react';
import { DashboardFrame } from './dashboard_frame';
import { ClientManager } from './subApps/cm/cm.js'
import { DirectMessage } from './subApps/dm.js'
import { AppointmentManager } from './subApps/am.js'
import { GroupManager } from './subApps/gm.js'
import { SettingsForm } from '../setup/settings.js';
import { getUserData } from '../database/database.js';



/* KEY: 1 = Cleint Manager, 2 = Direct Message, 3 = Appointment Manager, 4 = Group Manager */

export function RenderDashboard(props){
    const [currentPage, setPage] = useState(0)
    function handleNavButtonClick(i){
      setPage(i)
    }


    return (
  
      <>
      
        <DashboardFrame userData={props.userData} displayNum={currentPage} navButtonClicked={(i) => handleNavButtonClick(i)}>
          <DirectContent user={props.user} userData={props.userData} currentPage={currentPage} changeUserDataState={props.changeUserDataState}/>
        </DashboardFrame>
  
      </>
    );
}







function DirectContent(props){

  const handleSubscription = async () => {
    const response = await fetch('http://127.0.0.1:4242/create-checkout-session', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email: props.user.email, user_id: props.user.uid })
    });
    const session = await response.json();
    if (response.ok) {
        window.location.href = session.url;
    } else {
        console.error('Failed to create checkout session:', session);
    }
};

const checkSubscription = async () => {
  // Check if session_id is available in the props
  if (!props.userData.paymentData.session_id) {
    alert('Session ID is required');
    return; // Exit the function if session_id is not available
  }

  // Extract DBuserid and sessionId from props
  let DBuserid = props.user.uid;  // User ID from props
  let sessionId = props.userData.paymentData.session_id;

  try {
    // Construct the URL with both DBuserid and sessionId
    const url = `http://localhost:4242/check-subscription-status?DBuserid=${DBuserid}&session_id=${sessionId}`;
    const response = await fetch(url);
    const data = await response.json();

    // Check the response and display appropriate alert
    if (response.ok) {
      alert(data.message);
    } else {
      alert(data.error || 'Error occurred while checking subscription status');
    }
  } catch (error) {
    console.error('Failed to fetch subscription status:', error);
    alert('Failed to communicate with the server');
  }
};






  if (props.currentPage == 1){
    return <ClientManager userData={props.userData}></ClientManager>
  } else if (props.currentPage == 2){
    return <DirectMessage userData={props.userData}></DirectMessage>
  } else if (props.currentPage == 3){
    return <AppointmentManager userData={props.userData}></AppointmentManager>
  } else if (props.currentPage == 4){
    return <GroupManager userData={props.userData}></GroupManager>
  } else if (props.currentPage == 5){
    return <SettingsForm userData={props.userData} changeUserDataState={props.changeUserDataState}></SettingsForm>
  } else {
    return <>
  
    <button onClick={handleSubscription}>Checkout</button>
    <button onClick={checkSubscription}>Test Session</button>
    <button onClick={checkClientSubscription}>Test Subscription</button>
  </>
  }

}
