import React, { useState } from 'react';

const AddClientForm = ({ setCurrentDateTime }) => {
  const now = new Date();
  const [year, setYear] = useState(now.getFullYear());
  const [month, setMonth] = useState(now.getMonth() + 1);
  const [day, setDay] = useState(now.getDate());
  const [hour, setHour] = useState(now.getHours() % 12 || 12);
  const [minute, setMinute] = useState(now.getMinutes());
  const [amPm, setAmPm] = useState(now.getHours() >= 12 ? 'PM' : 'AM');

  // Use setCurrentDateTime directly from props for the button's onClick handler

  return (
    <div className="userblock" id="addClientBlock">
      {/* Form fields */}

      <button onClick={setCurrentDateTime} className="set-current-time">Set to Current Time</button>

      {/* Other form elements */}
    </div>
  );
};

export default AddClientForm;
