import React, { useState } from 'react';
import '../../cssFiles/cm.css';
import { setData } from '../../database/database';
import searchIcon from '../../images/search.png';

export function ClientManager(props) {
  const [showAddClientForm, setShowAddClientForm] = useState(false);

  // State for date and time, preset to current time
  const now = new Date();
  const [year, setYear] = useState(now.getFullYear());
  const [month, setMonth] = useState(now.getMonth() + 1); // JavaScript months are 0-indexed
  const [day, setDay] = useState(now.getDate());
  const [hour, setHour] = useState(now.getHours() % 12 || 12); // Adjust for 12-hour format, where 0 becomes 12
  const [minute, setMinute] = useState(now.getMinutes());
  const [amPm, setAmPm] = useState(now.getHours() >= 12 ? 'PM' : 'AM');

  const toggleAddClientForm = () => setShowAddClientForm(!showAddClientForm);

  // Helper to generate options for select inputs
  const generateOptions = (start, end) => {
    const options = [];
    for (let i = start; i <= end; i++) {
      options.push(<option key={i} value={i}>{i}</option>);
    }
    return options;
  };

  return (
    <>
      <div className="cm-top-headers">
        <div id="cm-toggle-add-client" onClick={toggleAddClientForm}>Add Client</div>
        <div id="cm-search"><h1>Search</h1><img id='cm-search-icon' src={searchIcon} /></div>
      </div>

      {showAddClientForm && (
        <div className="userblock" id="addClientBlock">
          <input type="text" className="firstName" placeholder="First Name" />
          <input type="text" className="lastName" placeholder="Last Name" />
          <input type="text" className="phoneNumber" placeholder="Phone Number" />

          {/* Styled Date and Time Inputs */}
          <div className="date-time-selectors" style={{ display: 'flex', gap: '10px' }}>
            <select className="date-input" value={month} onChange={(e) => setMonth(e.target.value)}>
              {generateOptions(1, 12)} {/* Months */}
            </select>
            <select className="date-input" value={day} onChange={(e) => setDay(e.target.value)}>
              {generateOptions(1, 31)} {/* Days */}
            </select>
            <select className="date-input" value={year} onChange={(e) => setYear(e.target.value)}>
              {generateOptions(now.getFullYear() - 5, now.getFullYear() + 5)} {/* Years */}
            </select>
            <select className="date-input" value={hour} onChange={(e) => setHour(e.target.value)}>
              {generateOptions(1, 12)} {/* Hours */}
            </select>
            <select className="date-input" value={minute} onChange={(e) => setMinute(e.target.value)}>
              {generateOptions(0, 59)} {/* Minutes */}
            </select>
            <select className="date-input" value={amPm} onChange={(e) => setAmPm(e.target.value)}>
              <option value="AM">AM</option>
              <option value="PM">PM</option>
            </select>
          </div>

          <div className="groupcontainer">
            <div className="addgroup">Add Group</div>
          </div>
          <div className="editstack">
            <button className="addClient">Confirm Client</button>
          </div>
        </div>
      )}
    </>
  );
}
