import searchIcon from '../../../images/search.png'
import React, { useState, useEffect } from 'react';


export function AddUser(props) {

    // groupNames looks like this ["group1", "group2", "group3"]
    var groupNames = props.userData.groupData



    // State to control the visibility of CMAddClient
    const [showAddClient, setShowAddClient] = useState(false);

    // Function to toggle the visibility
    const toggleAddClient = () => {
        setShowAddClient(!showAddClient);
    };

    return (
        <>
            <CMHeader toggleAddClient={toggleAddClient} />

            {showAddClient && <CMAddClient props />}
        </>
    );
}





function CMAddClient(props) {

    const { groupNames } = props;



    // Existing state and functions from your code...
    const [showGroupPopup, setShowGroupPopup] = useState(false);
    const [selectedGroups, setSelectedGroups] = useState([]);

    // Toggle the group popup
    const toggleGroupPopup = () => {
        setShowGroupPopup(!showGroupPopup);
    };

    // Handle group checkbox change
    const handleGroupChange = (groupName) => {
        setSelectedGroups((prevSelectedGroups) => {
            if (prevSelectedGroups.includes(groupName)) {
                // Remove the group if it's already selected
                return prevSelectedGroups.filter(group => group !== groupName);
            } else {
                // Add the group if it's not selected
                return [...prevSelectedGroups, groupName];
            }
        });
    };

    // Render the popup for group selection
    const renderGroupPopup = () => (
        <div className="group-popup" style={{ position: 'absolute', zIndex: 10, background: 'white', padding: '20px', border: '1px solid black' }}>
            {groupNames.map((groupName, index) => (
                <div key={index}>
                    <input
                        type="checkbox"
                        id={`group-${index}`}
                        name={groupName}
                        value={groupName}
                        checked={selectedGroups.includes(groupName)}
                        onChange={() => handleGroupChange(groupName)}
                    />
                    <label htmlFor={`group-${index}`}>{groupName}</label>
                </div>
            ))}
            <button onClick={() => setShowGroupPopup(false)}>Add Groups</button>
        </div>
    );








    // Initialize state with current date and time
    const now = new Date();
    let initialHour = now.getHours();
    const initialMinute = now.getMinutes();
    const initialAmpm = initialHour >= 12 ? 'PM' : 'AM';

    // Convert 24-hour format to 12-hour format
    initialHour = initialHour % 12;
    initialHour = initialHour ? initialHour : 12; // the hour '0' should be '12'

    const [date, setDate] = useState({
        day: now.getDate(),
        month: now.getMonth() + 1, // JavaScript months are 0-based
        year: now.getFullYear(),
        hour: initialHour,
        minute: initialMinute < 10 ? `0${initialMinute}` : initialMinute,
        ampm: initialAmpm,
    });

    // Handle change in date and time selectors
    const handleChange = (e) => {
        const { name, value } = e.target;
        setDate({ ...date, [name]: value });
    };

    return (
        <>
            <div className="userblock" id="addClientBlock">
                <input type="text" className="firstName" placeholder="First Name"/>
                <input type="text" className="lastName" placeholder="Last Name"/>
                <input type="text" className="phoneNumber" placeholder="Phone Number"/>

                <div className="appointment-date-container">
                    <div className="appointment-date-label">Appointment Date</div>
                    <div className="date-selectors">
                        <select name="day" className="daySelector" value={date.day} onChange={handleChange}>
                            {[...Array(31).keys()].map(day => (
                            <option key={day} value={day + 1}>{day + 1}</option>
                            ))}
                        </select>
                        <div className="date-separator">/</div>
                        <select name="month" className="monthSelector" value={date.month} onChange={handleChange}>
                            {['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'].map((month, index) => (
                            <option key={index} value={index + 1}>{month}</option>
                            ))}
                        </select>
                        <div className="date-separator">/</div>
                        <input name="year" type="text" className="yearSelector" placeholder="Year" value={date.year} onChange={handleChange}/>
                    </div>
                    <div className="time-selectors">
                        <select name="hour" className="hourSelector" value={date.hour} onChange={handleChange}>
                            {[...Array(12).keys()].map(hour => (
                            <option key={hour} value={hour + 1}>{hour + 1}</option>
                            ))}
                        </select>
                        <div className="time-separator">:</div>
                        <select name="minute" className="minuteSelector" value={date.minute} onChange={handleChange}>
                            {[...Array(60).keys()].map(minute => (
                            <option key={minute} value={minute < 10 ? `0${minute}` : minute}>
                                {minute < 10 ? `0${minute}` : minute}
                            </option>
                            ))}
                        </select>
                        <select name="ampm" className="ampmSelector" value={date.ampm} onChange={handleChange}>
                            <option value="AM">AM</option>
                            <option value="PM">PM</option>
                        </select>
                    </div>
                </div>

                <div className="groupcontainer">
                    <div className="addgroup" onClick={toggleGroupPopup}>Add Group</div>
                    {selectedGroups.map((groupName, index) => (
                        <div key={index} className="groupblock">{groupName}</div>
                    ))}
                </div>
                <div className="editstack">
                    <button className="addClient">Add</button>
                </div>
            </div>

            {showGroupPopup && renderGroupPopup()}
        </>
    );
}









function CMHeader(props) {

    return (
        <>
            <div className="cm-top-headers">
                <div id="cm-toggle-add-client" onClick={props.toggleAddClient}>Add Client</div>
                <div id="cm-search">
                    <h1>Search</h1>
                    <img id='cm-search-icon' src={searchIcon} alt="Search Icon" />
                </div>
            </div>
        </>
    );
}
