import React, { useState, useEffect } from 'react';
import "../../cssFiles/cm.css";
import { setData } from '../../database/database';
import searchIcon from '../../images/search.png';

export function ClientManager(props) {
  const [addClientVisible, setAddClientVisible] = useState(false);
  const [clients, setClients] = useState([]);
  const [groupData, setGroupData] = useState([]);
  const [newClient, setNewClient] = useState({ firstName: '', lastName: '', phoneNumber: '', date: '', groups: [] });

  useEffect(() => {
    const clientData = JSON.parse(props.userData.clientData);
    setClients(clientData);
    setGroupData(props.userData.groupData);
  }, [props.userData]);

  const handleToggleAddClient = () => {
    setAddClientVisible(!addClientVisible);
  };

  const handleAddClient = () => {
    // Example of setting new client data - needs proper date handling
    const updatedClients = [newClient, ...clients];
    setData({ ...props.userData, clientData: JSON.stringify(updatedClients) }); // Assuming setData updates the state
    setAddClientVisible(false);
  };

  const handleClientChange = (e, field) => {
    setNewClient({ ...newClient, [field]: e.target.value });
  };

  // Method to render client blocks
  const renderClientBlocks = () => {
    return clients.map((client, index) => (
      <div className="userblock" key={index}>
        <h3 className="firstName">{client.firstName}</h3>
        <h3 className="lastName">{client.lastName}</h3>
        <h3 className="phoneNumber">{client.phoneNumber}</h3>
        {/* Date handling and group rendering omitted for brevity */}
        <div className="editstack">
          <button className="edit">Edit</button>
          <button className="delete">Delete</button>
        </div>
      </div>
    ));
  };

  return (
    <>
      <div className="cm-top-headers">
        <div id="cm-toggle-add-client" onClick={handleToggleAddClient}>Add Client</div>
        <div id="cm-search"><h1>Search</h1><img id='cm-search-icon' src={searchIcon} /></div>
      </div>

      {addClientVisible && (
        <div className="userblock" id="addClientBlock">
          <input type="text" className="firstName" placeholder="First Name" value={newClient.firstName} onChange={(e) => handleClientChange(e, 'firstName')} />
          <input type="text" className="lastName" placeholder="Last Name" value={newClient.lastName} onChange={(e) => handleClientChange(e, 'lastName')} />
          <input type="text" className="phoneNumber" placeholder="Phone Number" value={newClient.phoneNumber} onChange={(e) => handleClientChange(e, 'phoneNumber')} />
          {/* Date inputs and handling omitted for brevity */}
          <div className="editstack">
            <button className="addClient" onClick={handleAddClient}>Confirm Client</button>
          </div>
        </div>
      )}

      {renderClientBlocks()}
    </>
  );
}
